def maxArea(heights):
    if len(heights) == 0:
        return 0

    left = 0
    right = len(heights) - 1
    max_area = 0

    while left <= right:
        width = right - left
        height_diff = max(heights[left], heights[right]) - min(heights[left], heights[right])
        current_area = width * height_diff

        if current_area > max_area:
            max_area = current_area

