def longest_palindrome(s):
    if not s:
        return ""

    start = 0
    max_length = 1

    for i in range(len(s)):
        r = i
        while r < len(s) - 1 and s[r + 1] == s[r]:
            r += 1
        i = r
        l, r = i, i
        while l > 0 and r < len(s) - 1 and s[l - 1] == s[r + 1]:
            l -= 1
            r += 1
        if r - l + 1 > max_length:
            start = l
            max_length = r - l + 1

