def is_interleave(s1, s2, s3):
    if len(s1) + len(s2) != len(s3):
        return False

    dp = [[False] * (len(s2) + 1) for _ in range(len(s1) + 1)]

    dp[0][0] = True
    for i in range(1, len(s1) + 1):
        if s1[i - 1] == s3[i - 1]:
            dp[i][0] = dp[i - 1][0]
        else:
            break
    for j in range(1, len(s2) + 1):
        if s2[j - 1] == s3[j - 1]:
            dp[0][j] = dp[0][j - 1]
        else:
            break

    for i in range(1, len(s1) + 1):
        for j in range(1, len(s2) + 1):
            if s1[i - 1] == s3[i + j - 1]:
                dp[i][j] = dp[i-1][j]
            if s2[j - 1] == s3[i + j - 1]:
                dp[i][j] = dp[i][j] or dp[i][j-1]

    return dp[-1][-1]

