def max_area(matrix):
    if not matrix:
        return 0

    rows = len(matrix)
    cols = len(matrix[0])
    height_array = [0] * (cols + 1)
    stack = []
    maxArea = 0
    current_index = 0
    top_index = -1

    for row in range(rows):
        for col in range(cols):
            if matrix[row][col] == '1':
                height_array[col] += 1
            else:
                height_array[col] = 0

        while stack and height_array[stack[-1]] < height_array[current_index]:
            top_index = stack.pop()
            area = (current_index - stack[-1] - 1) * height_array[top_index]
            maxArea = max(maxArea, area)

