from collections import Counter
def minWindow(s, t):
    if not s or not t: return ''  # Edge case check
    need = dict()
    window = dict()
    for i in range(len(t)):
        if (t[i] in need and need[t[i]] > 0) :
            need[t[i]] -= 1
        else:
            return ''

    left, right = 0, 0
    valid = 0
    start, length = -1, float('inf') # Edge case check for no window found
    while right < len(s):
        c = s[right]

        if (c in need and need[c] > 0) :
            window[c] = window.get(c,0) + 1

        # If all characters are included check the validity of current Window   
        while valid == len(t):
            if (right - left + 1 < length):  
                start ,length = left, right - left + 1
            c_prev = s[left]
            if (c_prev in need and window[c_prev] >0) :
                window[c_prev] -= 1
            else:
                return ''

            left += 1   
        right += 1   # Slide the Window incrementing Right pointer

    if start == -1: return '' # Edge case check for no window found at all
    else : return s[start:length]
