def solve(matrix):
    rows = len(matrix)
    cols = len(matrix[0])

    # Flag first row and column if zero present
    flag_row = False
    flag_col = False

    # Mark first element of each row and column for every 0 encountered
    mark_rows = [False] * rows
    mark_cols = [False] * cols

    for i in range(rows):
        for j in range(cols):
            if matrix[i][j] == 0:
                mark_rows[i] = True
                mark_cols[j] = True

                # Set flags if first row or column has a 0
                if i == 0:
                    flag_row = True
                if j == 0:
                    flag_col = True

    # Iterate over matrix, ignoring the first row and first column. 
    # Set the element to 0 if the corresponding first row or column is marked.
    for i in range(1, rows):
        for j in range(1, cols):
            if mark_rows[i] or mark_cols[j]:
                matrix[i][j] = 0

    # Finally, set the first row and column to 0 if their flags are set
    if flag_row:
        matrix[0] = [0] * cols
    if flag_col:
        for i in range(rows):
            matrix[i][0] = 0

    return matrix

