def uniquePaths(obstacles):
    if obstacles[0][0] == 1:
        return 0

    row = len(obstacles)
    col = len(obstacles[0])

    # Initialize first cell as 1 and process the first row and column
    for i in range(row):
        if obstacles[i][0] != 1:
            break
        obstacles[i][0] = 1
    for j in range(col):
        if obstacles[0][j] != 1:
            break
        obstacles[0][j] = 1

    # Iterate through the grid and set each cell's value as the sum of its two adjacent free cells above it and to its left
    for i in range(1, row):
        for j in range(1, col):
            if obstacles[i][j] == 1:
                above = obstacles[i-1][j] if i > 0 else 0
                left = obstacles[i][j-1] if j > 0 else 0
                obstacles[i][j] = above + left

    return obstacles[row-1][col-1]
