def findCombinations(candidates, startIndex, remaining):
    if remaining == 0:
        return [[]]
    if startIndex >= len(candidates) or remaining < 0:
        return []

    result = []
    # Recursive call excluding the current candidate and incrementing start index
    excludedResult = findCombinations(candidates, startIndex + 1, remaining)
    for combination in excludedResult:
        result.append(combination)

    # Recursive call including the current candidate and decreasing remaining accordingly
    includedResult = findCombinations(candidates, startIndex + 1, remaining - candidates[startIndex])
    for combination in includedResult:
        result.append([candidates[startIndex]] + combination)

    return result

def combinationSum(candidates, target):
    return findCombinations(candidates, 0, target)

