def find_substring_indexes(s, words):
    if not s or not words:
        return []

    word_count = {}
    for word in words:
        if word in word_count:
            word_count[word] += 1
        else:
            word_count[word] = 1

    word_length = len(words[0])
    total_words = len(words)
    total_length = sum(len(word) for word in words)

    result = []

    for i in range(0, len(s) - total_length + 1):
        temp_word_count = {}

        j = 0
        while j < total_words and i + (j * word_length) < len(s):
            current_word = s[i + (j * word_length):i + (j * word_length) + word_length]

            if current_word in word_count:
                if current_word not in temp_word_count or temp_word_count[current_word] < word_count[current_word]:
                    temp_word_count[current_word] = word_count[current_word]
                else:
                    break
            else:
                break

            j += 1

        if j == total_words:
            result.append(i)

    return result
