def merge2Lists(l1: ListNode, l2: ListNode) -> ListNode:
    if not l1 or not l2:
        return l1 if l1 else l2

    if l1.val < l2.val:
        l1.next = merge2Lists(l1.next, l2)
        return l1
    else:
        l2.next = merge2Lists(l1, l2.next)
        return l2

def mergeKLists(lists: list[ListNode]) -> ListNode:
    if not lists:
        return None

    while len(lists) > 1:
        merged_lists = []

        for i in range(0, len(lists), 2):
            if i + 1 < len(lists):
                l1 = lists[i]
                l2 = lists[i+1]
                merged_list = merge2Lists(l1, l2)
                merged_lists.append(merged_list)
            else:
                merged_lists.append(lists[i])

        lists = merged_lists

    return lists[0]

