def removeNthFromEnd(head: ListNode, n: int) -> ListNode:
    first = head
    second = head

    # Move the first pointer ahead by n positions
    for i in range(n):
        if not first:
            return None  # If there are no nodes left after moving 'n' steps, return None

        first = first.next

    while first:
        first = first.next
        second = second.next  # Move both pointers one step at a time until the first pointer reaches the end

    if not second:  
        head = head.next
    else:
        second.next = second.next.next  

    return head
