def roman_to_int(s):
    mapping = {'I': 1, 'V': 5, 'X': 10, 'L': 50, 'C': 100, 'D': 500, 'M': 1000}
    total = 0
    for i in range(len(s)):
        if i > 0 and mapping[s[i]] > mapping[s[i - 1]]:
            total += mapping[s[i]] - 2 * mapping[s[i - 1]]
        else:
            total += mapping[s[i]]
    return total

