def roman_to_decimal_converter(pairs):
    # Precomputed mapping for Roman numerals to decimals
    roman_map = {
        'M': 1000, 'D': 500, 'C': 100, 'L': 50,
        'X': 10, 'V': 5, 'I': 1
    }

    result = []

    # Iterate through each pair in the input list
    for decimal_value, roman_numeral in pairs:

        # Convert Roman numeral to decimal equivalent using precomputed mapping
        num = decimal_value
        while num > 0:
            if roman_numeral == "":
                break
            for i in range(len(roman_numeral)):
                if roman_map[roman_numeral[i]] < num:
                    result.append((num, roman_numeral[:i+1]))
                    num -= roman_map[roman_numeral[i]]
                    roman_numeral = roman_numeral[i+1:]
                    break

    return result

