import sys

def calculate_integer(input_str):
    result = 0
    i = 0
    sign = 1

    while i < len(input_str) and input_str[i] == ' ':
        i += 1

    if i == len(input_str):
        return 0

    if input_str[i] in ['+', '-']:
        sign *= -1 if input_str[i] == '-' else 1
        i += 1

    while i < len(input_str) and input_str[i].isdigit():
        result = (result * 10) + int(input_str[i])
        i += 1

    if abs(result * sign) > sys.maxsize:
        return sys.maxsize if sign > 0 else -sys.maxsize - 1

    return result * sign
