def longest_palindrome(s):
    if not s:
        return ""

    start = 0
    maxLength = 1

    for i in range(len(s)):
        l, r = i, i

        while r < len(s) - 1 and s[r + 1] == s[r]:
            r += 1
        i = r

        while l > 0 and r < len(s) - 1 and s[l - 1] == s[r + 1]:
            l -= 1
            r += 1

        if maxLength < r - l + 1:
            start, maxLength = l, r - l + 1

    return s[start:start + maxLength]
