def can_interleave(s1, s2, s3):
    m, n = len(s1), len(s2)
    if m + n < len(s3):
        return False

    dp = [[False] * (n + 1) for _ in range(m + 1)]

    for i in range(m + 1):
        for j in range(n + 1):
            if i == 0 and j == 0:
                dp[i][j] = True
            elif i > 0 and j > 0:
                if s1[i - 1] == s3[j + i - 1]:
                    dp[i][j] = dp[i - 1][j]
                if s2[j - 1] == s3[j + i - 1]:
                    dp[i][j] = (dp[i][j] or dp[i][j - 1])
            elif i > 0:
                if s1[i - 1] == s3[j + i - 1]:
                    dp[i][j] = dp[i - 1][j]
            elif j > 0:
                if s2[j - 1] == s3[j + i - 1]:
                    dp[i][j] = dp[i][j - 1]

    return dp[m][n]

