def modify_matrix(matrix):
    # Handle edge case: empty matrix
    if not matrix:
        return []

    m = len(matrix)
    n = len(matrix[0])

    # Mark rows and columns where zeros are encountered
    first_row = False
    first_col = False
    for i in range(m):
        for j in range(n):
            if matrix[i][j] == 0:
                matrix[0][j] = -1
                matrix[i][0] = -1

    # Propagate zeros based on marked rows and columns
    for i in range(1, m):
        for j in range(1, n):
            if matrix[i][j] == 0:
                if matrix[0][j-1] == -1 or matrix[i-1][0] == -1:
                    matrix[i][j] = 0

    # Adjust flags for first row and column
    for j in range(n):
        if matrix[0][j] == -1:
            first_col = True
    for i in range(m):
        if matrix[i][0] == -1:
            first_row = True

    # Set first row and column to 0 if their flags are set
    if first_row:
        matrix[0] = [0]*n
    if first_col:
        for i in range(m):
            matrix[i][0] = 0

    return matrix

