def binary_exponentiation(x: int, n: int) -> int:
    if not isinstance(n, int):
        raise ValueError("n must be an integer")

    result = 1
    temp_result = x

    while n > 0:
        if n & 1 == 1:  # current bit is set (i.e., n has odd parity)
            result *= temp_result

