def skip_n_nodes(head, n):
    if not head or n <= 0:
        return head

    first = second = head

    # Move first pointer n steps ahead
    for _ in range(n - 1):
        first = first.next

    while first and first.next:
        first = first.next
        second = second.next

    # If first pointer reached end, update head
    if not first:
        return second

    # Update next pointer of second pointer to skip n nodes
    temp = second
    for _ in range(n):
        temp = temp.next

    second.next = temp

    return head
