def findClosestSum(nums, target):
    if not nums:
        raise ValueError("Input array cannot be empty")

    nums.sort()

    closest = sum(nums[:3])

    for i in range(len(nums) - 2):
        left = i + 1
        right = len(nums) - 1

        while left < right:
            cur_sum = nums[i] + nums[left] + nums[right]

            if cur_sum == target:
                return cur_sum

            diff = abs(target - cur_sum)
            if diff < abs(target - closest):
                closest = cur_sum

            if cur_sum < target:
                left += 1
            else:
                right -= 1

    return closest
