    return common_prefix_helper(strings)

def common_prefix_helper(strings):
    min_len = min(len(s) for s in strings)
    prefix = ""

    for i in range(min_len):
        char = strings[0][i]
        for s in strings[1:]:
            if s[i] != char:
                return prefix

        prefix += char

    # if we reach the end of the first string without finding a mismatch
    return strings[0]

