def longest_palindrome(s):
    if not s:
        return ""

    start = 0
    maxLength = 0

    for i in range(len(s)):
        l = r = i
        while r < len(s) and s[r] == s[l]:
            r += 1
        i = r - 1

        while l >= 0 and r < len(s) and s[l] == s[r]:
            l -= 1
            r += 1

        if r - l > maxLength:
            start = i - (r - l) // 2
            maxLength = r - l

    return s[start:start + maxLength]
